const btn=document.querySelector(".search_btn"),searchEl=document.querySelector(".search"),input=document.querySelector(".search_input"),background=document.querySelector(".search_section"),songList=document.querySelector(".song_list"),songSection=document.querySelector(".song"),loader=document.querySelector(".loader"),message=document.querySelector(".message"),searchIcon=document.querySelector(".search_icon");input.value="";let searchTimeout;async function loadSongs(e){try{let s=`https://itunes.apple.com/search?term=${e}&entity=song`,n=await fetch(s),t=await n.json();0===t.resultCount?(loader.classList.remove("loader--active"),songList.innerHTML="",showNoResultsMessage(input.value)):displaySongList(t.results)}catch{loader.classList.remove("loader--active"),songList.innerHTML="",showErrorMessage()}}function findSongs(){let e=input.value.trim();e.length>0?(songList.classList.remove("song_list--hide"),searchTimeout=setTimeout(()=>{loadSongs(e)},500)):songList.classList.add("song_list--hide")}function displaySongList(e){loader.classList.remove("loader--active"),songList.innerHTML="",message.innerHTML="",e.forEach((e,s)=>{if(s<5){let n=document.createElement("li");n.classList.add("song_item"),n.innerHTML=`<span class="bold">${e.trackName}</span> - <span >${e.artistName}</span>`,songList.appendChild(n)}})}function showNoResultsMessage(e){message.innerHTML=`Nema rezultata za pojam <span class="message_input">"${e}"</span>`}function showErrorMessage(){message.innerHTML="Greška. Pokušaj ponovno."}function clearMessage(){message.innerHTML=""}background.addEventListener("click",function(e){e.target.classList.contains("search_section")&&0===input.value.length&&(searchIcon.innerHTML='<use xlink:href="img/sprite.svg#icon-search"></use>',searchEl.classList.remove("search--active"),btn.classList.remove("search_btn--remove"),input.blur())}),window.addEventListener("keydown",function(e){"Escape"===e.key&&0===input.value.trim().length?(searchEl.classList.remove("search--active"),btn.classList.remove("search_btn--remove"),searchIcon.innerHTML='<use xlink:href="img/sprite.svg#icon-search"></use>',input.blur()):"Escape"===e.key&&(input.value="",songList.innerHTML="",clearMessage(),btn.classList.add("search_btn--remove"))}),btn.addEventListener("click",function(){if(0===input.value.trim().length){searchEl.classList.toggle("search--active"),btn.classList.add("search_btn--remove");let e=function(){input.focus()};setTimeout(e,1200)}else 0!==input.value.trim().length&&(input.value="",songList.innerHTML="",clearMessage(),btn.classList.add("search_btn--remove"))}),input.addEventListener("input",function(){loader.classList.add("loader--active"),clearMessage(),clearTimeout(searchTimeout),findSongs(),0===input.value.trim().length?(loader.classList.remove("loader--active"),songList.innerHTML="",btn.classList.add("search_btn--remove")):0!==input.value.trim().length&&(searchIcon.innerHTML='<use xlink:href="img/sprite.svg#icon-close"></use>',btn.classList.remove("search_btn--remove"))});
